import React from 'react';
import './';

class Contador extends React.Component {
  state = {
    count: 0
  };

  componentDidMount() {
    this.funcionIncremento();
    this.funcionDecremento();
    this.funcionReset();
  }

  componentDidUpdate() {
    console.log("Ciclo de vida : DidUpdate");
  }

  componentWillUnmount() {
    console.log("Ciclo de vida : WillUnmount");
  }

  funcionIncremento = () =>
    this.setState({ count: this.state.count + this.props.incremento });

  funcionDecremento = () =>
    this.setState({ count: this.state.count + this.props.decremento });

  funcionReset = () => this.setState({ count: 0 });

  render() {
    return (
      <div>
        <h1>contador: {this.state.count}</h1>
        <div>
          <button className="contador" onClick={this.funcionDecremento}>
            Decremento
          </button>
          <button className="contador" onClick={this.funcionReset}>
            Reset
          </button>
          <button className="contador" onClick={this.funcionIncremento}>
            Incremento
          </button>
        </div>
      </div>
    );
  }
}

export default function App() {
  let numberUp = prompt("Por favor indica el valor a incrementar: ");
  let numberDown = prompt("Por favor indica el valor a decrementar: ");
  numberUp = numberUp | 0;
  numberDown = numberDown | 0;
  return (
    <div className="App">
      <h2>Seleccione alguna de las opciones: </h2>
      <span>
        <Contador incremento={numberUp} decremento={numberDown} />
      </span>
      <h4>Valor a incrementar: {numberUp}</h4>
      <h4>Valor a decrementar: {numberDown}</h4>
    </div>
  );
}
