import React from "react";
import moment from "moment";
import { hotelsData } from "./data";
import Hotels from "./Hotels";
import "../styles/styles.css";


export default class Filter extends React.Component {
  state = {
    fechaInicial: this.props.fechaInicial,
    fechaFinal: this.props.fechaFinal,
    hotels: hotelsData,
    hotelsFilter:[]
  };

  handleFechaInicialChange = (e) => {
    this.setState({ fechaInicial: e.target.value });
  };
  handleFechaFinalChange = (e) => {
    this.setState({ fechaFinal: e.target.value });
  };

  filterSizes = (selectedSizes) => {
    if (selectedSizes === "Hotel pequeño") {
      this.setState({
        hotels: this.state.hotelsFilter.filter((sizes) => sizes.rooms <= 10)
      });
    }
    if (selectedSizes === "Hotel mediano") {
      this.setState({
        hotels: this.state.hotelsFilter.filter(
          (sizes) => sizes.rooms > 10 && sizes.rooms <= 20
        )
      });
    }
    if (selectedSizes === "Hotel grande") {
      this.setState({
        hotels: this.state.hotelsFilter.filter((sizes) => sizes.rooms > 20)
      });
    }
  };

  handleFilters = () => {
    var comboCountry = document.getElementById("country");
    var selectedCountry = comboCountry.options[comboCountry.selectedIndex].text;
    var comboPrices = document.getElementById("prices");
    var selectedPrices = comboPrices.options[comboPrices.selectedIndex].text;
    var comboSizes = document.getElementById("sizes");
    var selectedSizes = comboSizes.options[comboSizes.selectedIndex].text;
    /*Primera Condicion */
    if (
      selectedCountry === "Todos los Paises" &&
      selectedPrices === "Cualquier Precio" &&
      selectedSizes === "Cualquier Tamaño"
    ) {
      this.setState({
        hotels: hotelsData
      });
    }
    /*Segunda Condicion */
    if (
      selectedCountry === "Todos los Paises" &&
      selectedPrices === "Cualquier Precio" &&
      selectedSizes !== "Cualquier Tamaño"
    ) {
      this.setState({
        hotelsFilter: hotelsData
      });
      this.filterSizes(selectedSizes);
      
    }
    /*Tercera Condicion*/
    if (
      selectedCountry === "Todos los Paises" &&
      selectedPrices !== "Cualquier Precio" &&
      selectedSizes === "Cualquier Tamaño"
    ) {
      this.setState({
        hotels: hotelsData.filter((filter) => filter.price === selectedPrices)
      });
    }
    /*Cuarta Condicion*/
    if (
      selectedCountry === "Todos los Paises" &&
      selectedPrices !== "Cualquier Precio" &&
      selectedSizes !== "Cualquier Tamaño"
    ) {
      this.setState({
        hotelsFilter: hotelsData.filter(
          (filter) => filter.price === selectedPrices
        )
      });
      this.filterSizes(selectedSizes);
    }
    /*Quinta Condicion*/
    if (
      selectedCountry !== "Todos los Paises" &&
      selectedPrices === "Cualquier Precio" &&
      selectedSizes === "Cualquier Tamaño"
    ) {
      this.setState({
        hotels: hotelsData.filter(
          (filter) => filter.country === selectedCountry
        )
      });
    }
    /*Sexta Condicion*/
    if (
      selectedCountry !== "Todos los Paises" &&
      selectedPrices === "Cualquier Precio" &&
      selectedSizes !== "Cualquier Tamaño"
    ) {
      this.setState({
        hotelsFilter: hotelsData.filter(
          (filter) => filter.country === selectedCountry
        )
      });
      this.filterSizes(selectedSizes);
    }
    /*Septima Condicion*/
    if (
      selectedCountry !== "Todos los Paises" &&
      selectedPrices !== "Cualquier Precio" &&
      selectedSizes === "Cualquier Tamaño"
    ) {
      this.setState({
        hotels: hotelsData.filter(
          (filter) =>
            filter.country === selectedCountry &&
            filter.price === selectedPrices
        )
      });
    }
    /*Octava Condicion*/
    if (
      selectedCountry !== "Todos los Paises" &&
      selectedPrices !== "Cualquier Precio" &&
      selectedSizes !== "Cualquier Tamaño"
    ) {
      this.setState({
        hotelsFilter: hotelsData.filter(
          (filter) =>
            filter.country === selectedCountry &&
            filter.price === selectedPrices
        )
      });
      this.filterSizes(selectedSizes);
    }
  };

  /* Filtros Independientes */

  /*
  handleCountries = () => {
    var combo = document.getElementById("country");
    var selected = combo.options[combo.selectedIndex].text;
    selected === "Todos los Paises"
      ? this.setState({
          hotels: hotelsData
        })
      : this.setState({
          hotels: hotelsData.filter(countries => countries.country === selected)
        });
  };

  handlePrices = () => {
    var combo = document.getElementById("prices");
    var selected = combo.options[combo.selectedIndex].text;
    selected === "Cualquier Precio"
      ? this.setState({
          hotels: hotelsData
        })
      : this.setState({
          hotels: hotelsData.filter(prices => prices.price === selected)
        });
  };
  handleSizes = () => {
    var combo = document.getElementById("sizes");
    var selected = combo.options[combo.selectedIndex].text;
    if (selected === "Cualquier Tamaño") {
      this.setState({
        hotels: hotelsData
      });
    }
    if (selected === "Hotel pequeño") {
      this.setState({
        hotels: hotelsData.filter(sizes => sizes.rooms <= 10)
      });
    }
    if (selected === "Hotel mediano") {
      this.setState({
        hotels: hotelsData.filter(
          sizes => sizes.rooms > 10 && sizes.rooms <= 20
        )
      });
    }
    if (selected === "Hotel grande") {
      this.setState({
        hotels: hotelsData.filter(sizes => sizes.rooms > 20)
      });
    }
  };
  */

  render() {
    moment.locale("es");
    var dateTime1 = moment(this.state.fechaInicial);
    // formato de fecha
    var full1 = dateTime1.format("dddd D, MMMM YYYY");
    var dateTime2 = moment(this.state.fechaFinal);
    // formato de fecha
    var full2 = dateTime2.format("dddd D, MMMM YYYY");
    /* Otra forma de sacar las fechas
    var fechaInicial = new Date(this.state.fechaInicial);
    var optionsInicial = {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric"
    };
    var fechaFinal = new Date(this.state.fechaFinal);
    var optionsFinal = {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric"
    };
*/

    const hotels = this.state.hotels.map((hotels) => (
      <Hotels
        slug={hotels.slug}
        key={hotels.slug}
        photo={hotels.photo}
        name={hotels.name}
        description={hotels.description}
        city={hotels.city}
        country={hotels.country}
        rooms={hotels.rooms}
        price={hotels.price}
      />
    ));
    return (
      <React.Fragment>
        <div className="header_container">
          <header className="header">
            <div>
              <h1>{this.props.titulo}</h1>
              <b>
                Desde el {full1}, hasta el {full2}
              </b>
            </div>
          </header>
          <div className="filter_description">
            <h3>Busca tus vacaciones ideales, en el lugar ideal</h3>
            <p>Con hoteles muy acogedores y especiales</p>
          </div>
          <section className="filters">
            <form className="fecha_inicial">
              <div>
                <label name="bday" />
                <input
                  className="fecha"
                  type="date"
                  id="bday"
                  name="bday"
                  value={this.state.fechaInicial}
                  required
                  pattern="[0-9]{4}-[0-9]{2}-[0-9]{2}"
                  onChange={this.handleFechaInicialChange}
                />
                <span className="validity" />
              </div>
            </form>

            <form className="fecha_final">
              <div>
                <label name="bday" />
                <input
                  className="fecha"
                  type="date"
                  id="bday"
                  name="bday"
                  value={this.state.fechaFinal}
                  required
                  pattern="[0-9]{4}-[0-9]{2}-[0-9]{2}"
                  onChange={this.handleFechaFinalChange}
                />
                <span className="validity" />
              </div>
            </form>

            <form className="container_countries">
              <div>
                <label name="country" />
                <select
                  className="select_country"
                  id="country"
                  name="country"
                  defaultValue="Todos los Paises"
                  onChange={this.handleFilters}
                >
                  <option>Todos los Paises</option>
                  <option>Argentina</option>
                  <option>Brasil</option>
                  <option>Chile</option>
                  <option>Uruguay</option>
                </select>
              </div>
            </form>

            <form className="container_prices">
              <div>
                <label name="prices" />
                <select
                  className="select_prices"
                  id="prices"
                  name="prices"
                  defaultValue="Cualquier Precio"
                  onChange={this.handleFilters}
                >
                  <option>Cualquier Precio</option>
                  <option>$</option>
                  <option>$$</option>
                  <option>$$$</option>
                  <option>$$$$</option>
                </select>
              </div>
            </form>

            <form className="container_sizes">
              <div>
                <label name="sizes" />
                <select
                  className="select_sizes"
                  id="sizes"
                  name="sizes"
                  defaultValue="Cualquier Tamaño"
                  onChange={this.handleFilters}
                >
                  <option>Cualquier Tamaño</option>
                  <option>Hotel pequeño</option>
                  <option>Hotel mediano</option>
                  <option>Hotel grande</option>
                </select>
              </div>
            </form>
          </section>
        </div>
        <div className="hotels">{hotels}</div>
      </React.Fragment>
    );
  }
}
